void initialize_vehicle(int v_id){

    enter_time_array[v_id] = pr_cl * step;
    enter_velocity_array[v_id] = 12 + random(5);

    if(dir==1 && lane ==1){

        c1 = 10; c2 = 9; c3= 8; c4 = 7; lane_number = 1;
        d1 = 5.25; d2 = 4.68; d3 = 1.135; d4 = 4.68;
    }
    else if(dir==1 && lane ==2){
        c1 = 11; c2 = 13; c3 = 14; c4 = 3; lane_number = 2;
        d1 = 5.43; d2 = 1.20; d3 = 5.988; d4 = 1.2;
    }
    else if(dir==2 && lane ==1){
        c1 = 4; c2 = 3; c3 = 2; c4 = 1; lane_number = 3;
        d1 = 5.25; d2 = 4.68; d3 = 1.135; d4 = 4.68;
    }
    else if(dir==2 && lane ==2){
        c1 = 5; c2 = 15; c3 = 16; c4 = 9;  lane_number = 4;
        d1 = 5.43; d2 = 1.20; d3 = 5.988; d4 = 1.2;
    }
    else if(dir==3 && lane ==1){
        c1 = 1; c2 = 12; c3 = 11; c4 = 10; lane_number = 5;
        d1 = 5.25; d2 = 4.68; d3 = 1.135; d4 = 4.68;
    }
    else if(dir==3 && lane ==2){
        c1 = 2; c2 = 14; c3 = 15; c4 = 6; lane_number = 6;
        d1 = 5.43; d2 = 1.20; d3 = 5.988; d4 = 1.2;
    }
    else if(dir==4 && lane ==1){
        c1 = 7; c2 = 6; c3 = 5; c4 = 4; lane_number = 7;
        d1 = 5.25; d2 = 4.68; d3 = 1.135; d4 = 4.68;
    }
    else if(dir==4 && lane ==2){
        c1 = 8; c2 = 16; c3 = 13; c4 = 12; lane_number = 8;
        d1 = 5.43; d2 = 1.20; d3 = 5.988; d4 = 1.2;
    }
//    dir_array[v_id] = dir;
    d1_array[v_id] = d1; d2_array[v_id] = d2; d3_array[v_id] = d3; d4_array[v_id] = d4;
    c1_array[v_id] = c1; c2_array[v_id] = c2; c3_array[v_id] = c3; c4_array[v_id] = c4;
    lane_number_array[v_id] = lane_number;
    v_trans[v_id] = enter_velocity_array[v_id];
    in_lane[v_id] = true;
    in_intersection[v_id] = false;
    //intersection_travel_length = 2*d1 + d2 + d3 + d4;
}


void find_Vlane(int v_id){
    double v_temp = v_max;

    double tt1, tt2, s, depart_time, intersection_travel_length, in_time, in_velocity;

    in_time = enter_time_array[v_id];
    in_velocity = enter_velocity_array[v_id];

    tt1 = abs(v_temp - in_velocity) / acc; // In seconds  
    s = abs(v_temp*v_temp - in_velocity * in_velocity) / (2*acc); // In meters

    intersection_travel_length = 2*d1 + d2 + d3 + d4; // meters
    tt2 = (approach_length + depart_point_distance + intersection_travel_length - s) / v_temp; // seconds
    depart_time = in_time + tt1 + tt2; // seconds

    while((depart_time < last_vehicle_depart_time[lane_number] + safety_gap) && v_temp > 0){ // safety gap is 0.5 seconds
        v_temp = v_temp - 0.01;
        tt1 = abs(v_temp - in_velocity) / acc;
        s = abs(v_temp*v_temp - in_velocity * in_velocity) / (2*acc);
        tt2 = (approach_length + intersection_travel_length + depart_point_distance - s) / v_temp;
        depart_time = in_time + tt1 + tt2;
    }

    v_lane_array[v_id] = v_temp;
    v_lane = v_temp;

}



void find_Vfefs(int v_id){

    double in_velocity, in_time, tt1, tt2, s, v_temp, d, cp_in_time, v1, v2; 
    in_velocity = enter_velocity_array[v_id];
    in_time = enter_time_array[v_id];

    // For cp1
    v_temp = v_lane_array[v_id];
    d = d1_array[v_id];
    tt1 = abs(v_temp - in_velocity)/acc;
    s = abs(v_temp*v_temp - in_velocity*in_velocity)/(2*acc);
    tt2 = (approach_length - s + d) / v_temp;
    cp_in_time = in_time + tt1 + tt2;

    while((cp_in_time < cp_register[c1_array[v_id]][0][1] + safety_gap) && v_temp > 0){
        v_temp = v_temp - 0.01;
        tt1 = abs(v_temp - in_velocity)/acc;
        s = abs(v_temp*v_temp - in_velocity*in_velocity)/(2*acc);
        tt2 = (approach_length - s + d) / v_temp;
        cp_in_time = in_time + tt1 + tt2;
    }
    v1 = v_temp;

    // For cp2
    v_temp = v_lane_array[v_id];
    d = d1_array[v_id] + d2_array[v_id];
    tt1 = abs(v_temp - in_velocity)/acc;
    s = abs(v_temp*v_temp - in_velocity*in_velocity)/(2*acc);
    tt2 = (approach_length - s + d) / v_temp;
    cp_in_time = in_time + tt1 + tt2;

    while((cp_in_time < cp_register[c2_array[v_id]][0][1] + safety_gap) && v_temp > 0){
        v_temp = v_temp - 0.01;
        tt1 = abs(v_temp - in_velocity)/acc;
        s = abs(v_temp*v_temp - in_velocity*in_velocity)/(2*acc);
        tt2 = (approach_length - s + d) / v_temp;
        cp_in_time = in_time + tt1 + tt2;
    }
    v2 = v_temp;
    v_fefs = v_temp;
    v_fefs_array[v_id] = min(v1, v2);



}




int fint(double x){
    int i;
    for(i = 0; i<x; i++){}
    return i-1;
}

bool is_window_available(double vel, double in_vel, double in_time, double d, int c, double v_len){
    double tt1, tt2, s, cp_in_time, cp_out_time;
    int i;
    s = abs(in_vel - vel)/ acc;
    tt1  = abs(vel*vel - in_vel*in_vel)/(2*acc);
    tt2 = (approach_length - s + d) / vel;

    cp_in_time = in_time + tt1 + tt2;
    cp_out_time = cp_in_time + (v_len/vel);
    if(cp_in_time > cp_register[c][0][1]) return true;

    for(i = stack_length -1; i>0; i--){
        // loop_value += 1;        
        if(cp_register[c][i][1] < cp_in_time and cp_register[c][i-1][0] > cp_out_time) return true;        
    }
    return false;
}


void find_Vwindow(int v_id){
    double in_velocity, in_time, v_temp;
    int i;
    bool c1_clear = false;bool c2_clear = false; bool c3_clear = false; bool c4_clear = false;
    bool is_v_window_possible;
    double tt1, tt2, s, cp1_in_time, cp2_in_time, cp3_in_time, cp4_in_time;
    in_velocity  = enter_velocity_array[v_id]; in_time = enter_time_array[v_id];

    v_temp = v_lane_array[v_id];




    while(!(c1_clear&&c2_clear&&c3_clear&&c4_clear) && v_temp>0){

        c1_clear = is_window_available(v_temp, in_velocity, in_time, d1, c1, veh_length);
        c2_clear = is_window_available(v_temp, in_velocity, in_time, d1+d2, c2, veh_length);
        c3_clear = is_window_available(v_temp, in_velocity, in_time, d1+d2+d3, c3, veh_length);
        c4_clear = is_window_available(v_temp, in_velocity, in_time, d1+d2+d3+d4, c4, veh_length);

        v_temp = v_temp - 0.01;

    }
    if(v_temp > 0.1){ received_windowV += 1; v_window_array[v_id] = v_temp + 0.01;}
    else v_window_array[v_id] = 0;

    v_window = v_temp+0.01;

}

bool did_conflict_resolve(int v_id){
    double v_fefs = v_fefs_array[v_id];
    if(v_fefs >0){
        double tt1, tt2, s, cp3_in_time, cp3_out_time, cp4_in_time, cp4_out_time, reserved_cp3_in_time, reserved_cp3_out_time, reserved_cp4_in_time, reserved_cp4_out_time;
        double in_velocity, in_time, cp3_distance, cp4_distance;
        bool cp3_clear = false, cp4_clear = false;

        in_velocity = enter_velocity_array[v_id];
        in_time = enter_time_array[v_id];

        cp3_distance = d1 + d2 + d3;
        cp4_distance = d1 + d2 + d3 + d4;    

        tt1 = abs(v_fefs - in_velocity)/acc;
        s = abs(v_fefs*v_fefs - in_velocity*in_velocity)/(2*acc);
        tt2 = (approach_length - s + cp3_distance) / v_fefs;
        cp3_in_time = in_time + tt1 + tt2;
        cp3_out_time = cp3_in_time + (veh_length/v_fefs);

        tt2 = (approach_length -s + cp4_distance)/v_fefs;
        cp4_in_time = in_time + tt1 + tt2;
        cp4_out_time = cp4_in_time + (veh_length/v_fefs);

        reserved_cp3_in_time = cp_register[c3][0][0]; reserved_cp3_out_time = cp_register[c3][0][1];
        reserved_cp4_in_time = cp_register[c4][0][0]; reserved_cp4_out_time = cp_register[c4][0][1];

        if((cp3_in_time > reserved_cp3_in_time & cp3_in_time > reserved_cp3_out_time)||(cp3_out_time < reserved_cp3_in_time && cp3_out_time < reserved_cp3_out_time)){
            cp3_clear = true;       
        }

        if((cp4_in_time > reserved_cp4_in_time & cp4_in_time > reserved_cp4_out_time)||(cp4_out_time < reserved_cp4_in_time && cp4_out_time < reserved_cp4_out_time)){
            cp4_clear = true;       
        }

        if(cp3_clear && cp4_clear) return true;
        else return false;
    }
    else return(false);


}

double min3(double a, double b, double c){
    if(a<b){
        if(a<c) return a;
        else return c;
    }
    else{
        if(b>c) return c;
        else return b;
    }

}

void find_Vreservation(int v_id){
    double in_velocity, in_time, tt1, tt2, s, v_temp, d, cp_in_time, v3, v4; 
    in_velocity = enter_velocity_array[v_id];
    in_time = enter_time_array[v_id];

    // For cp3
    v_temp = v_lane_array[v_id];
    d = d1_array[v_id]+d2_array[v_id]+d3_array[v_id];
    tt1 = abs(v_temp - in_velocity)/acc;
    s = abs(v_temp*v_temp - in_velocity*in_velocity)/(2*acc);
    tt2 = (approach_length - s + d) / v_temp;
    cp_in_time = in_time + tt1 + tt2;

    while((cp_in_time < cp_register[c3_array[v_id]][0][1] + safety_gap) && v_temp > 0){
        v_temp = v_temp - 0.01;
        tt1 = abs(v_temp - in_velocity)/acc;
        s = abs(v_temp*v_temp - in_velocity*in_velocity)/(2*acc);
        tt2 = (approach_length - s + d) / v_temp;
        cp_in_time = in_time + tt1 + tt2;
    }
    v3 = v_temp;

    // For cp4
    v_temp = v_lane_array[v_id];
    d = d1_array[v_id]+d2_array[v_id]+d3_array[v_id]+d4_array[v_id];
    tt1 = abs(v_temp - in_velocity)/acc;
    s = abs(v_temp*v_temp - in_velocity*in_velocity)/(2*acc);
    tt2 = (approach_length - s + d) / v_temp;
    cp_in_time = in_time + tt1 + tt2;

    while((cp_in_time < cp_register[c4_array[v_id]][0][1] + safety_gap) && v_temp > 0){
        v_temp = v_temp - 0.01;
        tt1 = abs(v_temp - in_velocity)/acc;
        s = abs(v_temp*v_temp - in_velocity*in_velocity)/(2*acc);
        tt2 = (approach_length - s + d) / v_temp;
        cp_in_time = in_time + tt1 + tt2;
    }
    v4 = v_temp;

    v_reservation_array[v_id] = min3(v_fefs_array[v_id], v3, v4);
    v_reservation = v_reservation_array[v_id];
}







void find_Vfinal(int v_id){
    if(v_id % 10 == 0) v_final[v_id] = enter_velocity_array[v_id];
    else{
        if(v_window_array[v_id] > 0) v_final[v_id] = v_window_array[v_id];
        else{
            if(did_conflict_resolve(v_id)) v_final[v_id] = v_fefs_array[v_id];
            else v_final[v_id] = v_reservation_array[v_id];
        }
        v_f = v_final[v_id];
    }

}



void insert_in_stack(int c, double in_time, double out_time){
    int i, j, index;
    bool index_found = false;
    for(i = 0; i< stack_length; i++){
        if(in_time > cp_register[c][i][0]) {
            if(!index_found){
                index_found = true;
                index = i;
            }

        }
    }

    for(j = stack_length-1; j >= index; j--){
        cp_register[c][j+1][0] = cp_register[c][j][0];
        cp_register[c][j+1][1] = cp_register[c][j][1];
    }

    cp_register[c][index][0] = in_time;
    cp_register[c][index][1] = out_time;   
}

void update_registers(int v_id){
    double tt1, tt2, s, pass_time, in_time, in_velocity, v_fin;
    int lane_num;
    lane_num = lane_number_array[v_id];
    in_time = enter_time_array[v_id];
    in_velocity = enter_velocity_array[v_id];


    v_fin = v_final[v_id];

    //v_temp_array[veh_id] = v_final;

    // Update registers according to the v_final velocity
    if(v_fin > 0){
        tt1 = abs(in_velocity - v_fin) / acc;
        s = abs(in_velocity*in_velocity - v_fin*v_fin) / (2*acc);
        tt2 = (approach_length - s + 2*d1 + d2 + d3 + d4 + depart_point_distance) / v_fin;
        //depart_time_array[v_id] = arrival_times[v_id] + tt1 + tt2;

        last_vehicle_depart_time[lane_num] = in_time + tt1 + tt2;
        pass_time = veh_length / v_fin;

        tt2 = (approach_length - s + d1) / v_fin; 
        insert_in_stack(c1, in_time + tt1 + tt2, in_time + tt1 + tt2 + pass_time+safety_gap);

        tt2 = (approach_length - s + d1 + d2) / v_fin;
        insert_in_stack(c2, in_time + tt1 + tt2, in_time + tt1 + tt2 + pass_time+safety_gap);

        tt2 = (approach_length - s + d1 + d2 + d3) / v_fin;
        insert_in_stack(c3, in_time + tt1 + tt2, in_time + tt1 + tt2 + pass_time + safety_gap);

        tt2 = (approach_length - s + d1 + d2 + d3 + d4) / v_fin;
        insert_in_stack(c4, in_time + tt1 + tt2, in_time + tt1 + tt2 + pass_time+safety_gap);


    }
}


void update_lane_pose(int v_id){    
    double v_fin, v_temp, in_velocity,s;
    // entered_here = 1;
    // loop_value += 1;

    double delta = approach_length/3000.0;
    int lane_num = lane_number_array[v_id];
    double position;
    int roster, previous_roster,i, roster_overflow = 0;

    v_fin = v_final[v_id]; v_temp = v_trans[v_id]; in_velocity = enter_velocity_array[v_id];
    s = abs((v_fin*v_fin) - (in_velocity*in_velocity))/(2*acc);

    if(pose[v_id] < approach_length){
        if(pose[v_id] < s){
            if(v_fin > in_velocity){
                pose[v_id] = pose[v_id] + v_temp*step + (0.5*acc*step*step);
                v_temp = v_temp + acc*step;
                v_trans[v_id] = v_temp;
                if(v_trans[v_id] > v_fin) v_trans[v_id] = v_fin;

            }else{
                pose[v_id] = pose[v_id] + v_temp*step - (0.5*acc*step*step);
                v_temp = v_temp - acc*step;
                v_trans[v_id] = v_temp;              
                if(v_trans[v_id] < v_fin) v_trans[v_id] = v_fin;                  
            }
        }else{
            pose[v_id] = pose[v_id] + v_temp*step;
            // if(pose[v_id] > 150) pose[v_id] = 150.0;
            }
    }else{
        in_lane[v_id] = false;
        veh_in_int += 1;
        in_intersection[v_id] = true;
    }

    if(in_lane[v_id]){
        position = pose[v_id]; roster = fint(position/delta);
        previous_roster = previous_lane_roster[v_id];

        if(previous_roster < 50){
            for(i = 0; i < previous_roster; i++) lane_occupancy[lane_num][i] = 0;
        }else{
            for(i = 0; i < 50; i++) lane_occupancy[lane_num][previous_roster - i] = 0;
        }

        if(roster < 50){
            for(i = 0; i < roster; i++) lane_occupancy[lane_num][i] = 1;        
        }else{
            if(roster >= 3000) roster_overflow = roster - 3000;
            for(i = 0; i < 50 - roster_overflow - 1; i++) lane_occupancy[lane_num][roster - roster_overflow - 1 - i] = 1;
        }

        if(roster < 3000) previous_lane_roster[v_id] = roster;
        else previous_lane_roster[v_id] = 2999;
    }



}



void check_lane_collision(int v_id){    
    int lane_n = lane_number_array[v_id], i, array_size = 3000;
    int max_length = 0, current_length = 0;
    if(in_lane[v_id]){
        for(i = 0; i<array_size;i++){
        if(lane_occupancy[lane_n][i] == 1){
            current_length += 1;
        }else current_length = 0;

        if(current_length > max_length) max_length = current_length;
    }
    }
    if(max_length > 51) lane_collision += 1;


}

void update_int_pose(int v_id){
    double v_fin = v_final[v_id], int_length_travelled, total_int_length, delta;
    int lane_num = lane_number_array[v_id], roster_head, number_of_rosters, i;
    if(lane_num==1 || lane_num== 3 || lane_num== 5 || lane_num==7) {total_int_length = 21.0; number_of_rosters = 21*20;}
    else {total_int_length = 19.25; number_of_rosters = 385;} //19.25*20

    delta = total_int_length / number_of_rosters;
    pos = pose[v_id];
    if(in_intersection[v_id]){
        pose[v_id] = pose[v_id] + v_fin * step;
        int_length_travelled = pose[v_id] - 150.0;
        if(int_length_travelled >= total_int_length){
            in_intersection[v_id] = false;
            veh_in_int -= 1;
            // pose[v_id] = 150.0 + total_int_length;
        }
        else{
            roster_head = fint((int_length_travelled)/delta);
        }
    }

    if(previous_int_roster[v_id] < 50){
        for(i = 0; i < previous_int_roster[v_id]; i++) int_occupancy[lane_num][i] = 0;
    }else{
        for(i=0; i<50; i++) int_occupancy[lane_num][previous_int_roster[v_id] - i] = 0;
    }

    if(roster_head < 50){
            for(i = 0; i < roster_head; i++) int_occupancy[lane_num][i] = 1;        
        }else{
            for(i = 0; i < 50; i++) int_occupancy[lane_num][roster_head - i] = 1;
        }

        previous_int_roster[v_id] = roster_head;


}

void check_int_collision(int v_id){
    if(int_occupancy[1][109] && int_occupancy[5][316]) {int_collision += 1; cp = 1;}
    if(int_occupancy[1][199] && int_occupancy[4][277]) {int_collision += 1; cp = 2;}
    if(int_occupancy[1][222] && int_occupancy[8][109]) {int_collision += 1; cp = 3;}
    if(int_occupancy[1][316] && int_occupancy[7][106]) {int_collision += 1; cp = 4;}

    if(int_occupancy[3][316] && int_occupancy[5][106]) {int_collision += 1; cp = 5;}
